function [cellArray] = CtrCoupl(Children,Parent,NumSignals)    switch NumSignals        case 1            cellArray = {Parent,'1',Children{1},'1';                         Children{1},'1',Children{2},'1';                         Children{2},'1',Parent,'1'};         case 2            cellArray = {Parent,'1',Children{2},'1';                         Parent,'2',Children{1},'1';                         Children{1},'1',Children{2},'2';                         Children{2},'1',Parent,'1'};          case 3            cellArray = {Parent,'1',Children{2},'1';                         Parent,'2',Children{1},'1';                         Children{1},'1',Children{2},'2';                         Parent,'3',Children{2},'3';                         Children{2},'1',Parent,'1'};        otherwise            cellArray = cell(1,4);    endend