function [cellArray] = ModelCoupl(Children,NumSignals)    switch NumSignals        case 1            cellArray = {Children{1},'1',Children{2},'1';                         Children{2},'1',Children{3},'1'};        case 2            cellArray = {Children{1},'1',Children{2},'1';                         Children{1},'2',Children{2},'2';                         Children{2},'1',Children{3},'1'};         case 3            cellArray = {Children{1},'1',Children{2},'1';                         Children{1},'2',Children{2},'2';                         Children{1},'3',Children{2},'3';                         Children{2},'1',Children{3},'1'};        otherwise            cellArray = cell(1,4);    endend